import { useRef, useEffect } from 'react'
import LogoImage from '../../../assets/images/logo.png'
import './index.scss'
import gsap from 'gsap-trial'
import DrawSVGPlugin from 'gsap-trial/DrawSVGPlugin'

const Logo = () => {
  const bgRef = useRef()
  const outlineLogoRef = useRef()
  const logoRef = useRef()

  useEffect(() => {
    gsap.registerPlugin(DrawSVGPlugin)
    gsap
      .timeline()
      .to(bgRef.current, { duration: 1, opacity: 1 })
      .from('.cls-2', {
        drawSVG: 1,
        duration: 2,
      })
      .from(outlineLogoRef, {
        drawSVG: 0,
        duration: 1,
      })

    gsap.from('.cls-1', {
      delay: 1,
      drawSVG: 1,
      duration: 1,
    })

    gsap.timeline().play()
  }, [])

  return (
    <div className="logo-container" ref={bgRef}>
      <img ref={logoRef} className="solid-logo" src={LogoImage} alt="logo" />
      <svg
        id="Layer_1"
        data-name="Layer 1"
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 723.72 422.07"
      >
        <g className="svg-container">
          <path
            ref={outlineLogoRef}
            class="cls-2"
            d="M70.98,38c31.64,32.45,63.03,64.66,93.84,96.27-43.12,43.23-87.53,87.74-131.35,131.67-8.99-8.91-18.72-18.55-28.56-28.31,33.82-33.89,68.26-68.39,102.19-102.39C72.02,100.17,37.55,65.69,2.82,30.97,13.1,20.62,22.26,11.28,31.66,2.19c.61-.59,3.63,.3,4.7,1.35,11.55,11.31,22.94,22.78,34.62,34.46Z"
          />

          <path
            ref={outlineLogoRef}
            class="cls-2"
            d="M234.23,251.91c41.29,0,82.09,0,122.88,0q6.64,0,6.66,6.61c.01,11.49-.11,22.98,.07,34.47,.06,3.92-.94,5.62-5.28,5.61-58.44-.12-116.89-.11-175.33-.01-3.76,0-4.86-1.25-4.81-4.9,.18-12.32,.18-24.64,0-36.96-.05-3.69,1.13-4.92,4.86-4.88,16.82,.16,33.63,.07,50.95,.07Z"
          />

          <path
            ref={outlineLogoRef}
            class="cls-1"
            d="M360.61,373.01c5-1.12,6.5,.65,6.39,5.37-.29,12.66-.13,25.33-.08,37.99,0,2.9-.07,5.26-4.09,5.2-4.03-.06-3.48-2.66-3.48-5.13,0-12.83-.03-25.66,.03-38.49,0-1.59,.54-3.18,1.24-4.93Z"
          />
        </g>
      </svg>
    </div>
  )
}

export default Logo
